        return 1; // time1 is greater
    } else if (date1 < date2) {
        return -1; // time2 is greater
    } else {
        return 0; // times are equal
    }
}


// Function to parse time and compare the difference
function isWithinMinutes(time1, time2) {
    var date1 = new Date(time1);
    var date2 = new Date(time2);    

    var differenceInMinutes = Math.abs((date1.getTime() - date2.getTime()) / 1000 / 60);

    return differenceInMinutes <= 30;
}


// Function to compare two objects and return the differences
function getDifferences(obj1, obj2) {
    var diffs = {};
